mapper.select:
select u.user_id,u.username,u.loginname,u.password,u.rights as user_rights,r.role_id,r.role_name ,u.last_login,u.contact,u.phone,u.email,u.provincialid,u.cityid,u.countyid,u.address,u.post,u.tax,u.file,u.type
from tb_user u 
left join tb_role r on u.role_id=r.role_id 
where 1=1

	and u.loginname like "%"#{loginname}"%" 


	and u.role_id=#{roleId} 


and u.last_login>=#{lastLoginStart} 


and u.last_login<=#{lastLoginEnd}

select u.user_id,u.username,u.loginname,u.rights as user_rights,u.role_id,u.password,u.last_login,u.contact,u.phone,u.email,u.provincialid,u.cityid,u.countyid,u.address,u.post,u.tax,u.file,u.type, p.orgid proid,p.orgname proname,c.orgid cityid,c.orgname cityname,d.orgid countyid,d.orgname countyname from tb_user u left join organ p on u.provincialid = p.orgid and p.level = 1 and p.valid = 1 left join organ c on u.cityid = c.orgid and c.level = 2 and c.valid = 1 left join organ d on u.countyid = d.orgid and d.level = 3 and d.valid = 1 where u.status = 0
and loginname like "%"#{loginname}"%"
and username like "%"#{username}"%"
and password = #{password}
and user_id = #{userId}
and type = #{type}

select u.user_id,u.username,u.loginname,u.password,u.rights,u.role_id,u.status,u.last_login,u.contact,u.phone,u.email,u.provincialid,u.cityid,u.countyid,u.address,u.post,u.tax,u.file,u.type
from tb_user u
where 1=1

	and loginname = #{loginname}


	and password = #{password}


	and user_id = #{userId}

select count(user_id) from tb_user where 1=1 

	and loginname = #{loginname}


	and role_id=#{roleId} 


	and last_login=#{lastLoginStart} 


	and last_login=#{lastLoginEnd} 


	and tax=#{tax}

mapper.update:
update tb_user set 
loginname=#{loginname},
username=#{username},
role_id=#{roleId} 
,password=#{password} 
 where user_id=#{userId}
